<template>  <router-view/></template><style lang="less">  html,body{    background: #f5f5f5;  }  .hljs-attr{  	color: #0079f3;  }  .hljs-string{  	    color: #5758bb;  }  .hljs-keyword{  	color: #0079f3;  }  .hljs-variable{  	color: #88f;  }  .hljs-name{  	    color: #0079f3;  }  code {       color: #455a64;   }.hljs-tag{color: #0079f3	}.hljs-literal{	color: #32a973;}.hljs-comment{	    color: #999;	    font-size: 12px;}.hljs-attribute{	color: #e6550d;}.hljs-built_in{	color:#0079f3;}</style><script>  import {mapMutations,mapState} from 'vuex'  export default {    name: 'app',    computed:{      ...mapState([        'demoUrl',        'docsUrl'      ])    },    methods:{      ...mapMutations([        'SET_DEMO_URL'      ]),    },    watch:{      $route(to,from){        if(to.meta.isDemo){          this.SET_DEMO_URL(`#/demo/${to.name}`)        }else{          this.SET_DEMO_URL(`#/demo`)        }      },      docsUrl(newvalue){        if(newvalue !== '/docs'){          window.parent.location.href=`#${newvalue}`        }      }    }  }</script>