<template>  <demo-section>    <demo-block title="基本用法" class="demo_block">      <div style="text-align: center">        <x-circle                v-model="currentRate1"                :rate="rate"                :speed="100"                :text="currentRate1.toFixed(0) + '%'">        </x-circle>      </div>    </demo-block>    <demo-block title="样式定制" class="demo_block">      <div style="text-align: center">        <x-circle                v-model="currentRate2"                color="#13ce66"                fill="#fff"                size="120px"                layer-color="#eee"                :text="currentRate2.toFixed(0) + '%'"
                :rate="rate"                :speed="100"                :clockwise="false"                :stroke-width="60">        </x-circle>      </div><div>        <x-button          :text="adds"          type="primary"          size="small"          @click="add"        />        <x-button          :text="decrease"          type="danger"          size="small"          @click="reduce"        />      </div>    </demo-block>  </demo-section></template><script>const format = rate => Math.min(Math.max(rate, 0), 100);
	  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components:{      demoSection,      demoBlock    },    data() {      return {      	adds:'增加',
      	decrease:'减少',        rate: 30,
        currentRate1: 0,
        currentRate2: 0,      };    },    computed:{        },    methods:{ add() {      this.rate = format(this.rate + 20);    },    reduce() {      this.rate = format(this.rate - 20);    }    }  }</script><style scoped lang="less">  .demo_block{    .x-circle{      margin: 0 auto;    }  }</style>