<template>  <demo-section>    <demo-block title="消息提示">      <x-button plain type="primary" @click="showAlert"> 消息提示</x-button>
      <x-button type="primary"        plain        style="margin-left: 10px;"        @click="onClickAlert2"> 无标题提示</x-button>    </demo-block>    <demo-block title="confirm 确认">      <x-button type="warning" @click="showConfirm"> 弹出Confirm</x-button>    </demo-block>        <demo-block title="高级用法">      <x-button        type="danger"        plain        @click="show = true">       高级用法      </x-button>      <x-dialog        v-model="show"        show-cancel-button        :before-close="beforeClose"      >        <x-field          v-model="username"          :label="usernames"          :placeholder="usernamePlaceholder"        />        <x-field          v-model="password"          type="password"          :label="passwords"          :placeholder="passwordPlaceholder"        />      </x-dialog>    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components: {      demoSection,      demoBlock    },     data() {    return {      show: false,      username: '',
      usernames: '用户名',      password: '',
      passwords: '密码',      usernamePlaceholder:"请输入用户名",      passwordPlaceholder:"请输入密码"    };  },    methods:{      showAlert(){        this.$dialog.alert({          title:"标题",          message: '感谢对Nui的支持，在不断完善中'        });      },      onClickAlert2(){      	 this.$dialog.alert({        message:'感谢对Nui的支持，在不断完善中'      });      },       beforeClose(action, done) {      if (action === 'confirm') {        setTimeout(done, 1000);      } else {        done();      }    },      showConfirm(){        this.$dialog.confirm({          title: '标题',          message: '感谢对Nui的支持，在不断完善中'        }).then(() => {          // on confirm        }).catch(() => {          // on cancel        });      }    }  }</script><style scoped lang="less"></style>